const mondayList = document.getElementById('monday-list');
const newPeople = document.getElementById('new-people');
const submitBtn = document.getElementById('submit-btn');
const startBtn = document.getElementById('start-btn');
const listContainer = document.getElementById('list-container');
const randomContainer = document.getElementById('random-container');
const randomNames = document.getElementById('random-names');
const emojiField = document.getElementById('emoji-area');
const moderator = document.getElementById('moderator');

// init names
const mondayPeople = [
    'Flavius',
    'Bertha',
    'Lea',
    'Desmond',
    'Nikita',
    'Ulf',
    'Miguel'
]

// store listitems
const listItems = [];

// store the actual names for randomization
const listForRandomization = [];

//init count variable
let count = 0;

// create list out of existing names and append to monday-list
function createList() {

    [...mondayPeople]
        .forEach((person) => {
            const listItem = document.createElement('li');

            // create element
            listItem.innerHTML = `<input type="checkbox" checked="checked" class="list-item" id="${count}">
            <span class="name" id="${count}">${person}</span>`;

            // append element
            mondayList.appendChild(listItem);
            listItems.push(listItem);
            count++;
        })
}

// add new name to monday list
function addPeople() {
    if (newPeople.value !== '') {

        // create element
        const listItem = document.createElement('li');
        listItem.innerHTML = `<input type="checkbox" checked="checked" class="list-item" id="${count}">
        <span class="name" id="${count}">${newPeople.value}</span>`;

        //append element
        mondayList.appendChild(listItem);
        listItems.push(listItem);

        // clear input and increment counter
        newPeople.value = '';
        count++;
    }
    else {
        console.log('Please add a name');
    }
}

// check list for radio buttons before starting the randomizer
function checkList() {

    // collect all names in the list
    const allItems = document.querySelectorAll(".list-item");
    const allNames = document.querySelectorAll(".name");

    // loop through all names and check if radio button is checked and add to new list
    for (let i = 0; i < allItems.length; i++) {
        if (allItems[i].checked) {
            listForRandomization.push(allNames[i].innerHTML);
        }
    }
}

//start randomizer
function startRandomizer() {
    checkList();
    listContainer.style.display = 'none';
    randomContainer.style.display = 'flex';
    randomNames.style.display = 'flex';
    randomContainer.classList.add("random-container-open");
    randomLoop();
}

// start list
createList();

// eventlisteners for buttons
submitBtn.onclick = addPeople;
startBtn.onclick = startRandomizer;

/* randomizer */

// random function
function getRandomInt(max) {
    return Math.floor(Math.random() * max);
}

