// generate random emoji
function setEmoji() {
    emojiField.innerHTML = emojis[Math.floor(Math.random() * emojis.length)];
}

// init count for random loop
var i = 0;

// random loop for changing the name
function randomLoop() {
    setTimeout(function () {
        let length = listForRandomization.length;
        let j = getRandomInt(length);
        let randomName = listForRandomization[j];
        randomNames.innerText = randomName;    
        i++;
        // random loop calling itself twelve times
        if (i < 12) {
            randomLoop();
        } else {
            moderator.prepend('Moderator/in heute:');
            setEmoji();
        }
    }, 300)
}
